mapper.select:
SELECT id, user_id, start_date, end_date, meal_count, health_score,
created_at, ai_recommendation, ai_generated_at, health_tips
FROM weekly_reports
WHERE user_id = #{userId}
AND start_date = #{startDate}
AND end_date = #{endDate}

mapper.update:
UPDATE weekly_reports
SET ai_recommendation = #{aiRecommendation},
ai_generated_at = #{aiGeneratedAt}
WHERE id = #{id}

UPDATE weekly_reports
SET health_tips = #{healthTips}
WHERE id = #{id}
