mapper.select:
SELECT *
FROM meals
WHERE user_id = #{userId}

    AND eat_date BETWEEN #{startDate} AND #{endDate}


    AND time_slot = #{timeSlot}

ORDER BY eat_date DESC,
FIELD(time_slot, '아침', '점심', '저녁')

mapper.update:
UPDATE meals
SET eat_date = #{eatDate},time_slot = #{timeSlot},
photo_url = #{photoUrl},food_name = #{foodName},
memo = #{memo}
WHERE id=#{id}
