login_id = #{loginId}, password_hash = #{password}, name = #{name}, birth_date = #{birthDate}, gender = #{gender}, email = #{email}, height = #{height}, weight = #{weight}
, theme_pref = #{uiTheme}
WHERE id = #{id}
